CREATE OR REPLACE FUNCTION obter_regra_agenda_tipo_atend ( cd_agenda_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_tipo_atend_w	integer;
ds_Retorno_w		varchar(1)	:= 'S';
qt_regra_w		integer;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_agenda_tipo_atend
where	ie_situacao	= 'A';

if (qt_regra_w	> 0) then

	select	count(*)
	into STRICT	qt_regra_tipo_atend_w
	from	regra_agenda_tipo_atend
	where	ie_tipo_atendimento		= ie_tipo_atendimento_p
	and	coalesce(cd_agenda, cd_agenda_p)	= cd_agenda_p
	and	ie_situacao	= 'A';

	if (qt_regra_tipo_atend_w	= 0) then
		ds_Retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
