CREATE OR REPLACE FUNCTION obter_regra_alerta_agenda ( nr_seq_regra_p bigint, cd_agenda_p bigint, ie_status_agenda_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_registros_w		smallint;


BEGIN
ds_retorno_w := 'S';

select	count(*)
into STRICT	qt_registros_w
from	regra_envio_sms_agenda_cir
where	nr_seq_regra	= nr_seq_regra_p;

if (qt_registros_w > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	regra_envio_sms_agenda_cir
	where	nr_seq_regra	= nr_seq_regra_p
	and	coalesce(cd_agenda,coalesce(cd_agenda_p,0)) 	= coalesce(cd_agenda_p,0)
	and coalesce(ie_status_agenda,coalesce(ie_status_agenda_p,'XPTO')) = coalesce(ie_status_agenda_p,'XPTO');
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
