CREATE OR REPLACE FUNCTION obter_regra_alerta_mipres ( nr_seq_regra_p bigint, ie_tipo_item_presc_p text) RETURNS varchar AS $body$
DECLARE


ie_regra_w		varchar(1) := 'S';
qt_registros_w		bigint;

		

BEGIN

select	coalesce(max(1),0)
into STRICT	qt_registros_w
from	regra_envio_sms_mipres
where	nr_seq_regra	= nr_seq_regra_p;

if (qt_registros_w > 0) then
	select	coalesce(MAX('S'),'N')
	into STRICT	ie_regra_w
	from	regra_envio_sms_mipres
	where	nr_seq_regra	= nr_seq_regra_p
	and	ie_tipo_item_presc 	= ie_tipo_item_presc_p;
	
end if;	

return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
