CREATE OR REPLACE FUNCTION obter_regra_alter_status_exame (nr_seq_prescr_p bigint, nr_prescricao_p bigint, ie_status_atend_p bigint) RETURNS bigint AS $body$
DECLARE

nr_seq_exame_w		bigint;
ie_tipo_atend_w		bigint;
nr_seq_grupo_exame_w	bigint;
ie_retorno_w		smallint;

C01 CURSOR FOR 
	SELECT	coalesce(max(ie_regra),1) 
	from 	lab_regra_alteracao_status 
	where	ie_status_atend 				= ie_status_atend_p 
	and	coalesce(nr_seq_exame,nr_seq_exame_w)		= nr_seq_exame_w 
	and	coalesce(nr_seq_grupo_exame,nr_seq_grupo_exame_w) 	= nr_seq_grupo_exame_w 
	and	coalesce(ie_tipo_atendimento,ie_tipo_atend_w) 	= ie_tipo_atend_w 
	order  by nr_seq_exame, nr_seq_grupo_exame,ie_tipo_atendimento;


BEGIN 
 
select	a.nr_seq_exame, 
	c.ie_tipo_atendimento 
into STRICT	nr_seq_exame_w, 
	ie_tipo_atend_w 
from 	prescr_procedimento a, 
	prescr_medica b, 
	atendimento_paciente c 
where 	a.nr_sequencia 		= nr_seq_prescr_p 
and  	a.nr_prescricao 	= nr_prescricao_p 
and  	a.nr_prescricao 	= b.nr_prescricao 
and  	b.nr_atendimento 	= c.nr_atendimento;
 
select	a.nr_sequencia 
into STRICT	nr_seq_grupo_exame_w 
from 	grupo_exame_lab a, 
	exame_laboratorio b 
where 	b.nr_seq_exame 		= nr_seq_exame_w 
and  	b.nr_seq_grupo 		= a.nr_sequencia;
	 
open C01;
loop 
fetch C01 into	 
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_retorno_w := ie_retorno_w;
	exit;
	end;
end loop;
close C01;
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
