CREATE OR REPLACE FUNCTION obter_regra_antimicrobiano ( cd_material_p bigint, ie_objetivo_p text, cd_microorganismo_cih_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao
A - Amostra
M - microorganismo
*/
ds_retorno_w		varchar(255);

ie_antimicrobiano_w	varchar(1);
--ie_objetivo_w		varchar2(1);
--cd_microorganismo_cih_w	number(10);
count_w			bigint;
count_ww		bigint;

cd_amostra_cih_regra_w		bigint;
cd_amostra_cih_regra_ww		bigint;
cd_microorganismo_cih_regra_w	bigint;

c01 CURSOR FOR
SELECT	cd_amostra_cih,
	cd_microorganismo_cih
from	rep_regra_antimicrobiano
where	ie_objetivo = ie_objetivo_p
and	coalesce(cd_microorganismo_filtro,coalesce(cd_microorganismo_cih_p,0)) = coalesce(cd_microorganismo_cih_p,0)
order by ie_objetivo,
	coalesce(cd_microorganismo_filtro,0);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_antimicrobiano_w
from	material
where	ie_dias_util_medic <> 'N'
and	cd_material = cd_material_p;

if (ie_antimicrobiano_w = 'S') then

	select	count(*)
	into STRICT	count_w
	from	rep_regra_antimicrobiano
	where	ie_objetivo = ie_objetivo_p;

	if (count_w > 0) then
		/*select	max(cd_amostra_cih),
			max(cd_microorganismo_cih)
		into	cd_amostra_cih_regra_w,
			cd_microorganismo_cih_regra_w
		from	rep_regra_antimicrobiano
		where	ie_objetivo = ie_objetivo_p
		and	nvl(cd_microorganismo_filtro,cd_microorganismo_cih_p) = cd_microorganismo_cih_p;*/
		open C01;
		loop
		fetch C01 into
			cd_amostra_cih_regra_w,
			cd_microorganismo_cih_regra_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			cd_amostra_cih_regra_w		:= cd_amostra_cih_regra_w;
			cd_microorganismo_cih_regra_w	:= cd_microorganismo_cih_regra_w;
		end loop;
		close C01;

	end if;

	/*select	count(*)
	into	count_ww
	from	rep_regra_antimicrobiano
	where	ie_objetivo = ie_objetivo_p
	and	nvl(cd_microorganismo_filtro,cd_microorganismo_cih_p) = cd_microorganismo_cih_p;

	if	(count_ww > 0) then

		select	max(nvl(cd_amostra_cih,0))
		into	cd_amostra_cih_regra_ww
		from	rep_regra_antimicrobiano
		where	ie_objetivo = ie_objetivo_p
		and	nvl(cd_microorganismo_filtro,cd_microorganismo_cih_p) = cd_microorganismo_cih_p;

	end if;*/
end if;

if (ie_opcao_p = 'A') then
	ds_retorno_w := cd_amostra_cih_regra_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w := cd_microorganismo_cih_regra_w;
elsif (ie_opcao_p = 'AM') then
	ds_retorno_w := cd_amostra_cih_regra_w;
end if;

return	ds_retorno_w;

end;
$body$
