SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_arredondamento (cd_convenio_p bigint, cd_categoria_p text, cd_item_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, dt_vigencia_p timestamp, ie_tipo_item_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
1  - Forma de arredondamento
2 - Numero de casas Decimais
*/


/* ie_tipo_item_p
P - Procedimento
M- Material
*/
ds_retorno_w			varchar(5);
cd_grupo_mat_w			grupo_material.cd_grupo_material%type;
cd_subgrupo_mat_w		subgrupo_material.cd_subgrupo_material%type;
cd_classe_mat_w			classe_material.cd_classe_material%type;
ie_arredondamento_w		varchar(1);
nr_dec_unitario_w		conv_regra_arred_mat.nr_dec_unitario%type;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_grupo_proc_w			procedimento.cd_grupo_proc%type;
cd_especialidade_w		grupo_proc.cd_especialidade%type;
cd_area_procedimento_w		especialidade_proc.cd_area_procedimento%type;
dt_vigencia_w			timestamp;

C01 CURSOR FOR
	SELECT	ie_arredondamento,
		nr_dec_unitario
	from	conv_regra_arred_mat
	where	cd_estabelecimento = cd_estabelecimento_p
	and 	ie_situacao = 'A'
	and 	cd_convenio = cd_convenio_p
	and (coalesce(cd_material,coalesce(cd_item_p,0))	= coalesce(cd_item_p,0))
	and (coalesce(cd_grupo_material,coalesce(cd_grupo_mat_w,0))	= coalesce(cd_grupo_mat_w,0))
	and (coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_mat_w,0))  = coalesce(cd_subgrupo_mat_w,0))
	and (coalesce(cd_classe_material,coalesce(cd_classe_mat_w,0))	= coalesce(cd_classe_mat_w,0))
	and	coalesce(cd_categoria, coalesce(cd_categoria_p,'0')) = coalesce(cd_categoria_p,'0')
	and	dt_vigencia_w between coalesce(dt_inicio_vigencia, dt_vigencia_w) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(coalesce(dt_final_vigencia, dt_vigencia_w))
	order by
		coalesce(cd_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_categoria,'0');
		
C02 CURSOR FOR
	SELECT	ie_arredondamento		
	from	conv_regra_arred_proc
	where	cd_estabelecimento = cd_estabelecimento_p
	and 	ie_situacao = 'A'
	and 	cd_convenio = cd_convenio_p
	and (coalesce(cd_procedimento,coalesce(cd_item_p,0))	= coalesce(cd_item_p,0))
	and 	((((cd_procedimento IS NOT NULL AND cd_procedimento::text <> '') and (coalesce(ie_origem_proced,coalesce(ie_origem_proced_p,0))	= coalesce(ie_origem_proced_p,0)))) or (coalesce(cd_procedimento::text, '') = ''))
	and (coalesce(cd_area_procedimento,coalesce(cd_area_procedimento_w,0))	= coalesce(cd_area_procedimento_w,0))
	and (coalesce(cd_especialidade,coalesce(cd_especialidade_w,0))  = coalesce(cd_especialidade_w,0))
	and (coalesce(cd_grupo_proc,coalesce(cd_grupo_proc_w,0))	= coalesce(cd_grupo_proc_w,0))
	and	coalesce(cd_categoria, coalesce(cd_categoria_p,'0')) = coalesce(cd_categoria_p,'0')
	and	dt_vigencia_w between coalesce(dt_inicio_vigencia, dt_vigencia_w) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(coalesce(dt_final_vigencia, dt_vigencia_w))
	order by
		coalesce(cd_procedimento,0),
		coalesce(cd_area_procedimento,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_categoria,'0');
			

BEGIN

ds_retorno_w	:= '';
dt_vigencia_w	:= coalesce(dt_vigencia_p, clock_timestamp());

if (ie_tipo_item_p = 'M') then

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_mat_w,
		cd_subgrupo_mat_w,
		cd_classe_mat_w
	from	estrutura_material_v
	where	cd_material = cd_item_p;
	
	open C01;
	loop
	fetch C01 into	
		ie_arredondamento_w,
		nr_dec_unitario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
				
		if (ie_opcao_p = '1') then
			ds_retorno_w:= ie_arredondamento_w;
		elsif (ie_opcao_p = '2') then
			ds_retorno_w:= nr_dec_unitario_w;
		end if;
		
		end;
	end loop;
	close C01;

elsif (ie_tipo_item_p = 'P') then

	select	cd_area_procedimento,
		cd_especialidade,
		cd_grupo_proc
	into STRICT	cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_item_p
	and 	ie_origem_proced = ie_origem_proced_p;
	
	open C02;
	loop
	fetch C02 into	
		ie_arredondamento_w;		
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
				
		if (ie_opcao_p = '1') then
			ds_retorno_w:= ie_arredondamento_w;
		end if;
		
		end;
	end loop;
	close C02;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_regra_arredondamento (cd_convenio_p bigint, cd_categoria_p text, cd_item_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, dt_vigencia_p timestamp, ie_tipo_item_p text, ie_opcao_p text) FROM PUBLIC;

