CREATE OR REPLACE FUNCTION obter_regra_atend_local ( nr_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_local_estoque_w		local_estoque.cd_local_estoque%type;
cd_perfil_w			perfil.cd_perfil%type;
ie_permite_w			varchar(1) := 'S';

c01 CURSOR FOR
SELECT	coalesce(ie_permite,'S')
from	regra_atend_req_local
where	cd_local_estoque = cd_local_estoque_w
and (cd_perfil = cd_perfil_w or coalesce(cd_perfil::text, '') = '')
order by
	cd_local_estoque,
	coalesce(cd_perfil,99999) desc;


BEGIN

select	cd_local_estoque,
	obter_perfil_ativo
into STRICT	cd_local_estoque_w,
	cd_perfil_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_permite_w := ie_permite_w;
	end;
end loop;
close C01;


return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
