CREATE OR REPLACE FUNCTION obter_regra_ativacao_spa (nr_sequencia_p spa.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) RETURNS varchar AS $body$
DECLARE


nr_seq_tipo_spa_w	spa.nr_seq_tipo%type;
cd_centro_custo_spa_w	spa.cd_centro_custo%type;
nm_usuario_regra_w	spa_regra_visualizacao.nm_usuario_regra%type;
cd_perfil_w		spa_regra_visualizacao.cd_perfil%type;
cd_estabelecimento_spa_w	estabelecimento.cd_estabelecimento%type;
ds_retorno_w		varchar(1) := 'S';

c01 CURSOR FOR
	SELECT 	coalesce(nm_usuario_regra,0),
		coalesce(cd_perfil,0)
	from 	spa_regra_visualizacao
	where 	ie_situacao = 'A'
	and	coalesce(nr_seq_tipo, coalesce(nr_seq_tipo_spa_w,0)) = coalesce(nr_seq_tipo_spa_w,0)
	and	coalesce(cd_centro_custo, coalesce(cd_centro_custo_spa_w,0)) = coalesce(cd_centro_custo_spa_w,0)
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_spa_w,0)) = coalesce(cd_estabelecimento_spa_w,0);

BEGIN

select 	a.nr_seq_tipo,
	a.cd_centro_custo,
	(select cd_estabelecimento from centro_custo x where x.cd_centro_custo = a.cd_centro_custo) cd_estabelecimento
into STRICT	nr_seq_tipo_spa_w,
	cd_centro_custo_spa_w,
	cd_estabelecimento_spa_w
from 	spa a
where 	a.nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nm_usuario_regra_w,
	cd_perfil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	 ds_retorno_w := 'N';

	if (nm_usuario_regra_w = nm_usuario_p) then
		ds_retorno_w := 'S';
		exit;
	elsif (obter_se_perfil_usuario(cd_perfil_w, nm_usuario_p) = 'S') then
		ds_retorno_w := 'S';
		exit;
	end if;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
