CREATE OR REPLACE FUNCTION obter_regra_autor_gerar_atend (ie_autorizacao_p text, cd_agenda_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1) := 'S';


BEGIN

select	coalesce(max(ie_gerar_atend),'S')
into STRICT	ie_permite_w
from	agenda_regra_autor
where	cd_estabelecimento	= cd_estabelecimento_p
and	ie_autorizacao 		= ie_autorizacao_p
and	coalesce(cd_agenda,cd_agenda_p) = cd_agenda_p;

return	ie_permite_w;

end;
$body$
