CREATE OR REPLACE FUNCTION obter_regra_auto_viagem (cd_pessoa_viagem_p bigint, cd_pessoa_autorizador_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_p    1 : Autorização da viagem
	    2 : Aprovação do relatório de despesas */
ds_retorno_w		varchar(1)	:= 'N';
cd_setor_viagem_w	integer	:=  0;
qt_usuario_w		integer	:=  0;

C01 CURSOR FOR
	SELECT	coalesce(ie_autoriza_viagem,'N')
	from	via_regra_autorizacao
	where	coalesce(cd_setor_viagem, cd_setor_viagem_w) = cd_setor_viagem_w
	and 	cd_pessoa_autorizador = cd_pessoa_autorizador_p
	order by coalesce(cd_setor_viagem,0);

C02 CURSOR FOR
	SELECT	coalesce(ie_aprova_relatorio,'N')
	from	via_regra_autorizacao
	where	coalesce(cd_setor_viagem, cd_setor_viagem_w) = cd_setor_viagem_w
	and 	cd_pessoa_autorizador = cd_pessoa_autorizador_p
	order by coalesce(cd_setor_viagem,0);


BEGIN

if (cd_pessoa_viagem_p IS NOT NULL AND cd_pessoa_viagem_p::text <> '') then
	select 	count(*)
	into STRICT	qt_usuario_w
	from 	usuario
	where 	cd_pessoa_fisica = cd_pessoa_viagem_p;

	if (qt_usuario_w = 0) then
		ds_retorno_w:= 'S';
	else
		begin
		select 	coalesce(max(cd_setor_atendimento),0)
		into STRICT	cd_setor_viagem_w
		from 	usuario
		where 	cd_pessoa_fisica = cd_pessoa_viagem_p;
		exception
		when others then
			cd_setor_viagem_w	:= 0;
		end;
	end if;
end if;

if (cd_setor_viagem_w <> 0) then

	if (ie_tipo_p = '1') then

		ds_retorno_w	:= 'N';

		open C01;
		loop
		fetch C01 into
			ds_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ds_retorno_w:= 	ds_retorno_w;
			end;
		end loop;
		close C01;

	end if;

	if (ie_tipo_p = '2') then

		ds_retorno_w	:= 'N';

		open C02;
		loop
		fetch C02 into
			ds_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			ds_retorno_w:= 	ds_retorno_w;
			end;
		end loop;
		close C02;

	end if;

end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
