CREATE OR REPLACE FUNCTION obter_regra_avaliacao (nm_usuario_p text, vl_media_p bigint) RETURNS varchar AS $body$
DECLARE



vl_inicial_w	avf_resultado_avaliacao.vl_inicial%type;
vl_maximo_w		avf_resultado_avaliacao.vl_maximo%type;
ds_resultado_w 	avf_resultado_avaliacao.ds_resultado%type;



c01 CURSOR FOR
SELECT	vl_inicial,
		vl_maximo,
		ds_resultado
from	avf_resultado_avaliacao;

BEGIN

open	c01;
