CREATE OR REPLACE FUNCTION obter_regra_bloq_oci ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_bloqueio_oci_w		varchar(1);
qt_registros_w			integer;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	obter_se_mat_bloq_email(cd_material, cd_estabelecimento_p) = 'N';

if (qt_registros_w > 0) then
	ie_bloqueio_oci_w := 'S';
else
	ie_bloqueio_oci_w := 'N';
end if;

return	ie_bloqueio_oci_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
