CREATE OR REPLACE FUNCTION obter_regra_campo_autor ( ie_tipo_autorizacao_p text, nr_seq_estagio_p bigint, cd_perfil_p bigint, nm_usuario_p text, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE

ie_brigatorio_w varchar(1);

c01 CURSOR FOR
SELECT 	coalesce(ie_obrigatorio, 'N')
from 	regra_campo_autorizacao
where 	ie_tipo_autorizacao				= ie_tipo_autorizacao_p
and 	coalesce(nr_seq_estagio,coalesce(nr_seq_estagio_p,0))	= coalesce(nr_seq_estagio_p,0)
and 	coalesce(cd_perfil,coalesce(cd_perfil_p,0))		= coalesce(cd_perfil_p,0)
and 	coalesce(nm_usuario_regra,coalesce(nm_usuario_p,'X'))	= coalesce(nm_usuario_p,'X')
and 	nm_atributo				= nm_atributo_p
order by nm_usuario_regra desc, cd_perfil desc;


BEGIN

open c01;
loop
fetch c01 into
	ie_brigatorio_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return ie_brigatorio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
