CREATE OR REPLACE FUNCTION obter_regra_centro_custo_mat ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_regra_p text, ie_tipo_ordem_p text) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
qt_regra_w				integer;
cd_centro_custo_w				integer;
ie_controlado_w				varchar(1);
ie_padronizado_w				varchar(1);
cd_unidade_medida_compra_w		varchar(30);
cd_perfil_w				perfil.cd_perfil%type;

/* ie_regra_p
	O = Ordem de compras*/
c01 CURSOR FOR
SELECT	cd_centro_custo
from	regra_local_compra
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(cd_perfil, cd_perfil_w)				= cd_perfil_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	((coalesce(ie_controlado,'A') = 'A') or (coalesce(ie_controlado, 'A') 	= ie_controlado_w))
and	((coalesce(ie_padronizado,'A') = 'A') or (coalesce(ie_padronizado, 'A') 	= ie_padronizado_w))
and	coalesce(cd_unidade_medida, cd_unidade_medida_compra_w)	= cd_unidade_medida_compra_w
and	ie_ordem_compra = CASE WHEN ie_regra_p='O' THEN  'S'  ELSE 'N' END
and	coalesce(ie_tipo_ordem,ie_tipo_ordem_p) = ie_tipo_ordem_p
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

cd_perfil_w	:= Obter_Perfil_Ativo;

select	count(*)
into STRICT	qt_regra_w
from	regra_local_compra;

if (qt_regra_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		substr(obter_se_medic_controlado(cd_material),1,1) ie_controlado,
		substr(obter_se_material_padronizado(cd_estabelecimento_p, cd_material),1,1) ie_padronizado
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w,
		ie_controlado_w,
		ie_padronizado_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,255) cd_unidade_medida_compra
	into STRICT	cd_unidade_medida_compra_w
	from	material
	where	cd_material = cd_material_p;

	open c01;
	loop
	fetch c01 into
		cd_centro_custo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;
	end;
end if;

return cd_centro_custo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
