SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_cirurgia_conta (nr_interno_conta_p text) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w 		varchar(2);
qt_regra_w 		bigint;
cd_convenio_w		integer;

cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
qt_regra_cirurgia_w		bigint;

C01 CURSOR FOR 
	SELECT	cd_procedimento, 
		ie_origem_proced 
	from	procedimento_paciente 
	where	nr_interno_conta = nr_interno_conta_p 
	and	coalesce(nr_cirurgia::text, '') = '' 
	order by 1;


BEGIN 
 
ie_retorno_w := 'S';
 
SELECT 	coalesce(max(cd_convenio_parametro),0) 
into STRICT  cd_convenio_w    
FROM  	conta_paciente              
WHERE 	nr_interno_conta = nr_interno_conta_p;
 
select 	count(*) 
into STRICT 	qt_regra_w 
from 	conv_regra_exige_cir 
where 	cd_convenio = cd_convenio_w;
 
 
if (qt_regra_w > 0) then 
 
	-- Verificacao dos procedimentos da conta 
	open C01;
	loop 
	fetch C01 into 
		cd_procedimento_w, 
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select	count(*) 
		into STRICT	qt_regra_cirurgia_w 
		from	conv_regra_exige_cir 
		where	cd_procedimento = cd_procedimento_w 
		and 	ie_origem_proced = ie_origem_proced_w 
		and	cd_convenio = cd_convenio_w;
		 
		if (qt_regra_cirurgia_w > 0) then 
			ie_retorno_w := 'N';
			exit;
		end if;
		 
		end;
	end loop;
	close C01;
	 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_regra_cirurgia_conta (nr_interno_conta_p text) FROM PUBLIC;

