CREATE OR REPLACE FUNCTION obter_regra_config_paciente ( nr_seq_CONCOPA_p bigint, nm_usuario_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE

ie_permissao_w	varchar(1);

BEGIN
ie_permissao_w	:= 'L';
begin
	select	ie_permissao
	into STRICT	ie_permissao_w
	from	conf_comp_pac_permissao
	where	nr_seq_CONCOPA	= nr_seq_CONCOPA_p
	  and	nm_usuario_perm	= nm_usuario_p;
exception
	when no_data_found then
	begin
		select	ie_permissao
		into STRICT	ie_permissao_w
		from	conf_comp_pac_permissao
		where	nr_seq_CONCOPA	= nr_seq_CONCOPA_p
		  and	cd_perfil	= cd_perfil_p;
	exception
		when no_data_found then
			select	max(coalesce(ie_permissao,'L'))
			into STRICT	ie_permissao_w
			from	conf_comp_pac_permissao
			where	nr_seq_CONCOPA	= nr_seq_CONCOPA_p
			  and	coalesce(cd_perfil::text, '') = ''
			  and	coalesce(nm_usuario_perm::text, '') = '';
	end;
end;

return ie_permissao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
