CREATE OR REPLACE FUNCTION obter_regra_consiste_material ( cd_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Funcion para obter informações da Regras de consistência do cadastro materiais. Passar a opção que deseja saber se consiste
ie_opcao_p:
A = Anvisa
E = Material Estoque*/
cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_obriga_w				varchar(1) := 'N';
ie_estoque_w				varchar(1) := 'X';
ie_retorno_w				varchar(1) := 'N';


c01 CURSOR FOR
SELECT	coalesce(ie_obriga_val_anvisa,'N'),
	coalesce(ie_estoque,'X')
from	regra_consist_cad_mat
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and (coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p or cd_estabelecimento_p = 0)
order by	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0),
	coalesce(cd_estabelecimento, 0);



BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;


open C01;
loop
fetch C01 into
	ie_obriga_w,
	ie_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_obriga_w	:= ie_obriga_w;
	ie_estoque_w := ie_estoque_w;
	end;
end loop;
close c01;


if (ie_opcao_p = 'A') then
	ie_retorno_w := ie_obriga_w;
end if;
if (ie_opcao_p = 'E') then
	ie_retorno_w := ie_estoque_w;
end if;


return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
