CREATE OR REPLACE FUNCTION obter_regra_contab_nf_operacao ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



cd_operacao_nf_w	smallint;
ie_data_contab_nf_w	varchar(3);


BEGIN

ie_data_contab_nf_w	:= 'X';

select	cd_operacao_nf
into STRICT	cd_operacao_nf_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

select	coalesce(max(ie_data_contab_nf),'X')
into STRICT	ie_data_contab_nf_w
from	operacao_nota
where	cd_operacao_nf = cd_operacao_nf_w;

return	ie_data_contab_nf_w;

end;
$body$
