SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_conv_funcao () RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
cd_convenio_w		integer;

C01 CURSOR FOR
		SELECT	b.cd_convenio
		from	convenio b
		where   1 = 1
		and	not exists (  SELECT 	1
				     from 	conveio_estab_func_lib a
				     where	a.cd_convenio = b.cd_convenio);


BEGIN

ds_retorno_w := '';

open C01;
loop
fetch C01 into
	cd_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(cd_convenio_w,0) <> 0) then
		ds_retorno_w := ds_retorno_w || ', ' || cd_convenio_w;
	end if;

	end;
end loop;
close C01;

ds_retorno_w := substr(ds_retorno_w,3,length(ds_retorno_w));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
