CREATE OR REPLACE FUNCTION obter_regra_cor_mat_pepo (cd_material_p bigint, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE



cd_grupo_material_w 	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
ds_material_w		varchar(255);
ds_cor_w		varchar(15);
nr_sequencia_w		bigint	:= null;
ds_retorno_w		varchar(15);


C01 CURSOR FOR

	SELECT		nr_sequencia,
				ds_cor
	FROM		REGRA_COR_MATERIAL_PEPO
	WHERE		coalesce(cd_material,cd_material_p) 				= cd_material_p
	AND		coalesce(cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	AND		coalesce(cd_subgrupo_material,cd_subgrupo_material_w) 	= cd_subgrupo_material_w
	AND		coalesce(cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
	ORDER BY
			coalesce(cd_material,0),
			coalesce(cd_grupo_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_classe_material,0);


BEGIN

select 	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	substr(ds_material,1,240)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	ds_material_w
from 	estrutura_material_v
where 	cd_material		= cd_material_p;


open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ds_cor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_cor_w := ds_cor_w;
	nr_sequencia_w	:= nr_sequencia_w;
	end;
end loop;
close C01;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_sequencia_w;
else
	ds_retorno_w	:= ds_cor_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
