CREATE OR REPLACE FUNCTION obter_regra_custo_padrao_mat ( cd_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w			integer;
qt_regra_w				integer;
ie_forma_preco_w			smallint;
vl_preco_w				double precision;
vl_retorno_w				double precision;


/*ie_opcao_p
R= Regra
V= Valor
*/
c01 CURSOR FOR
SELECT	ie_forma_preco,
	vl_preco
from	REGRA_CUSTO_COMPRA_MAT
where	coalesce(cd_grupo_material, cd_grupo_material_w)     	= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) = cd_material_p or cd_material_p = 0)
and	cd_estabelecimento = cd_estabelecimento_p
order by
        coalesce(cd_material, 0),
        coalesce(cd_classe_material, 0),
        coalesce(cd_subgrupo_material, 0),
        coalesce(cd_grupo_material, 0);


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	REGRA_CUSTO_COMPRA_MAT
where	cd_estabelecimento = cd_estabelecimento_p;

if (qt_regra_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	open c01;
	loop
	fetch c01 into
		ie_forma_preco_w,
		vl_preco_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;
	end;
end if;

if (ie_opcao_p = 'R') then
	vl_retorno_w	:= ie_forma_preco_w;
elsif (ie_opcao_p = 'V') then
	vl_retorno_w	:= vl_preco_w;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
