CREATE OR REPLACE FUNCTION obter_regra_dados_banco (nr_seq_conta_banco_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nr_seq_carteira_cobr_tit_p bigint) RETURNS bigint AS $body$
DECLARE
		

ie_tipo_pessoa_w	varchar(1);
nr_seq_regra_w		regra_dados_conta_banco.nr_sequencia%type;
			

BEGIN

ie_tipo_pessoa_w	:= 'A';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	ie_tipo_pessoa_w	:= 'F';
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	ie_tipo_pessoa_w	:= 'J';
end if;

select	max(a.nr_sequencia)
into STRICT	nr_seq_regra_w
from	regra_dados_conta_banco a
where	coalesce(a.ie_tipo_pessoa,'A')	= coalesce(ie_tipo_pessoa_w,coalesce(a.ie_tipo_pessoa,'A'))
and	a.nr_seq_conta_banco		= nr_seq_conta_banco_p
and		coalesce(a.nr_seq_carteira,0) = coalesce(nr_seq_carteira_cobr_tit_p,0);

if (coalesce(nr_seq_regra_w,0) = 0) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	regra_dados_conta_banco a
	where	coalesce(a.ie_tipo_pessoa,'A')	= 'A'
	and	a.nr_seq_conta_banco		= nr_seq_conta_banco_p
	and		coalesce(a.nr_seq_carteira,0) = coalesce(nr_seq_carteira_cobr_tit_p,0);
end if;

return	nr_seq_regra_w;

end;
