CREATE OR REPLACE FUNCTION obter_regra_deducao_conv ( cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_tipo_calculo_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w		conv_regra_deducao.nr_sequencia%type;
dt_referencia_w		timestamp;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	conv_regra_deducao
	where	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	and	coalesce(cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)
	and	((ie_tipo_calculo = ie_tipo_calculo_p) or (coalesce(ie_tipo_calculo_p::text, '') = ''))
	and	dt_referencia_w between PKG_DATE_UTILS.get_Time(coalesce(dt_inicio_vigencia,dt_referencia_w),0) and
				PKG_DATE_UTILS.get_Time(coalesce(dt_fim_vigencia,dt_referencia_w),0) + 86399/86400
	order by	coalesce(cd_convenio,0),
		coalesce(cd_estabelecimento,0);


BEGIN

dt_referencia_w	:= coalesce(dt_referencia_p, clock_timestamp());

open c01;
loop
fetch c01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_seq_regra_w	:= nr_seq_regra_w;
end loop;
close c01;

return nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
