CREATE OR REPLACE FUNCTION obter_regra_depend_positivo (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_w		bigint;
qt_exames_regra_w	integer;


BEGIN

select max(nr_seq_exame)
into STRICT   nr_seq_exame_w
from prescr_procedimento
where nr_prescricao = nr_prescricao_p
and nr_sequencia = nr_seq_prescr_p;

select 	coalesce(count(*),0)
into STRICT 	qt_exames_regra_w
from 	exame_lab_dependente
where 	nr_seq_exame = nr_seq_exame_w
and 	ie_geracao = 3;

if (qt_exames_regra_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
