CREATE OR REPLACE FUNCTION obter_regra_desc_conta (cd_convenio_p bigint, vl_conta_p bigint) RETURNS bigint AS $body$
DECLARE


pr_desconto_w	double precision	:= 0;


BEGIN

select	coalesce(max(pr_desconto),0)
into STRICT	pr_desconto_w
from	convenio_regra_desconto
where	cd_convenio	= cd_convenio_p
and	vl_conta_p between VL_BASE_INICIAL and VL_BASE_FINAL;


return pr_desconto_w;

end;
$body$
LANGUAGE PLPGSQL
