CREATE OR REPLACE FUNCTION obter_regra_desc_orc_proc ( nm_usuario_p text, pr_desconto_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_exame_p bigint, nr_seq_proc_interno_p bigint) RETURNS bigint AS $body$
DECLARE


cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;
ie_permite_desc_w	varchar(1)	:= 'S';
pr_desconto_w		double precision	:= 0;

C01 CURSOR FOR
	SELECT	pr_desconto
	from	regra_desc_orcamento_proc
	where	coalesce(cd_procedimento, cd_procedimento_p)			= cd_procedimento_p
	and	coalesce(ie_origem_proced, ie_origem_proced_p)		= ie_origem_proced_p
	and	coalesce(cd_area_procedimento, cd_area_procedimento_w)	= cd_area_procedimento_w
	and	coalesce(cd_especialidade, cd_especialidade_w)		= cd_especialidade_w
	and	coalesce(cd_grupo_proc, cd_grupo_proc_w)			= cd_grupo_proc_w
	and	coalesce(nm_usuario_regra, nm_usuario_p)			= nm_usuario_p
	and	coalesce(cd_perfil, obter_perfil_ativo)			= obter_perfil_ativo
	order by
		coalesce(cd_procedimento,0),
		coalesce(ie_origem_proced,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0),
		coalesce(cd_perfil,0),
		coalesce(nm_usuario_regra,'0');


BEGIN

select	max(cd_area_procedimento),
	max(cd_especialidade),
	max(cd_grupo_proc)
into STRICT	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

open C01;
loop
fetch C01 into
	pr_desconto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	pr_desconto_w	:= pr_desconto_w;
	end;
end loop;
close C01;

return	pr_desconto_w;

end;
$body$
