CREATE OR REPLACE FUNCTION obter_regra_dia_gera_ciclo (ie_tipo_atendimento_p bigint, cd_perfil_p bigint, ie_opcao_p text, nm_usuario_p text, cd_protocolo_p bigint, nr_seq_medicacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := null;

C01 CURSOR FOR
	SELECT  substr(ie_regra,1,255)
	from 	regra_dias_oncologia
	where	coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and	coalesce(cd_perfil, coalesce(cd_perfil_p,0))			= coalesce(cd_perfil_p,0)
	and	coalesce(cd_protocolo, coalesce(cd_protocolo_p,0))		= coalesce(cd_protocolo_p,0)
	and	coalesce(nr_seq_medicacao, coalesce(nr_seq_medicacao_p,0))	= coalesce(nr_seq_medicacao_p,0)
	order by ie_regra;


BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := substr(ds_retorno_w,1,255);
	end;
end loop;
close C01;


return	substr(ds_retorno_w,1,255);

end;
$body$
