CREATE OR REPLACE FUNCTION obter_regra_disp_comp_sol ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_regra_disp_w		varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select	coalesce(max(ie_regra_disp),'S')
	into STRICT	ie_regra_disp_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia_solucao = nr_seq_solucao_p;

end if;

return	ie_regra_disp_w;

end;
$body$
