CREATE OR REPLACE FUNCTION obter_regra_disp_pepo ( ie_tipo_prescricao_p bigint, cd_material_p bigint ) RETURNS varchar AS $body$
DECLARE



ie_possui_regra_w	varchar(1):= 'N';
ie_dispensa_w		varchar(1):= 'S';
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
ie_tipo_prescricao_w	smallint;
ie_tipo_prescr_w	smallint;
ie_tipo_prescr_ww	smallint;

c01 CURSOR FOR
	SELECT	a.ie_tipo_prescr
	from	regra_ajuste_disp_pepo a,
		regra_ajuste_disp_mat b
	where	a.nr_sequencia 		  				= b.nr_seq_regra
	and	coalesce(b.cd_material,cd_material_p) 			= cd_material_p
	and	coalesce(b.cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	and	coalesce(b.cd_subgrupo_material,cd_subgrupo_material_w) 	= cd_subgrupo_material_w
	and	coalesce(b.cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
	order by coalesce(b.cd_material,99999) desc,
		 coalesce(b.cd_grupo_material,99999) desc,
		 coalesce(b.cd_subgrupo_material,99999) desc,
		 coalesce(b.cd_classe_material,99999) desc,
		 b.nr_sequencia;


BEGIN
if (ie_tipo_prescricao_p IS NOT NULL AND ie_tipo_prescricao_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_regra_w
	from	regra_ajuste_disp_pepo a,
		regra_ajuste_disp_mat b
	where	a.nr_sequencia 		= b.nr_seq_regra;


	if (ie_possui_regra_w = 'S') and (coalesce(cd_material_p,0) > 0) then
		ie_dispensa_w := 'N';

		cd_grupo_material_w	:= Obter_estrutura_material(cd_material_p,'G');
		cd_subgrupo_material_w	:= Obter_estrutura_material(cd_material_p,'S');
		cd_classe_material_w	:= obter_dados_material(cd_material_p,'CCLA');

		open c01;
		loop
		fetch c01 into
			ie_tipo_prescr_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			ie_tipo_prescr_ww	:= ie_tipo_prescr_w;
			end;
		end loop;
		close c01;

		if (ie_tipo_prescr_ww = ie_tipo_prescricao_p) then
			ie_dispensa_w := 'S';
		end if;
	end if;
end if;

return	ie_dispensa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
