CREATE OR REPLACE FUNCTION obter_regra_disp_sala_cirur ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_dia_semana_p bigint) RETURNS bigint AS $body$
DECLARE


qt_minutos_w		smallint 	:= 0;
ie_possui_regra_w	varchar(1)	:= 'N';
nr_seq_interno_w	bigint;



BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_possui_regra_w
from	regra_disp_sala_cirur;

if (ie_possui_regra_w = 'S') then
	select	coalesce(max(nr_seq_interno),0)
	into STRICT	nr_seq_interno_w
	from	unidade_atendimento
	where	cd_setor_atendimento 	= cd_setor_atendimento_p
	and	cd_unidade_basica	= cd_unidade_basica_p
	and	cd_unidade_compl	= cd_unidade_compl_p;

	if (ie_dia_semana_p <> 7) and (ie_dia_semana_p <> 8) then
		select	coalesce(max(qt_minutos),0)
		into STRICT	qt_minutos_w
		from	regra_disp_sala_cirur
		where	((ie_dia_semana 			= ie_dia_semana_p) or (ie_dia_semana = 9))
		and	coalesce(nr_seq_interno,nr_seq_interno_w) 	= nr_seq_interno_w;
	else
		select	coalesce(max(qt_minutos),0)
		into STRICT	qt_minutos_w
		from	regra_disp_sala_cirur
		where	ie_dia_semana 				= ie_dia_semana_p
		and	coalesce(nr_seq_interno,nr_seq_interno_w) 	= nr_seq_interno_w;
	end if;

end if;


return	qt_minutos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
