SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_email_nfe ( nr_seq_nota_p bigint, ie_tipo_nota_p text, ie_status_envio_p text) RETURNS bigint AS $body$
DECLARE


qt_nota_w		bigint;
nr_Seq_regra_nfe_w	bigint;
cd_cgc_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);
qt_regra_w		bigint;
cd_estabelecimento_w	nota_fiscal.cd_estabelecimento%type;
cd_operacao_nf_w		operacao_nota.cd_operacao_nf%type;


BEGIN

select	count(*)
into STRICT	qt_nota_w
from 	nota_fiscal n,
	operacao_nota o
where 	n.cd_operacao_nf = o.cd_operacao_nf
and	n.nr_sequencia = nr_seq_nota_p
and	((o.ie_nf_eletronica = 'S' AND ie_tipo_nota_p = '2') or
	(o.ie_nf_eletronica <> 'S' AND ie_tipo_nota_p = '1'));

nr_seq_regra_nfe_w:= 0;

if (qt_nota_w > 0) then

	select	cd_pessoa_fisica,
		cd_cgc,
		cd_estabelecimento,
		cd_operacao_nf
	into STRICT	cd_pessoa_fisica_w,
		cd_cgc_w,
		cd_estabelecimento_w,
		cd_operacao_nf_w
	from	nota_fiscal
	where	nr_Sequencia = nr_seq_nota_p;

	select	count(*)
	into STRICT	qt_regra_w
	from	regra_email_nfe
	where	((cd_pessoa_fisica = cd_pessoa_fisica_w) or (cd_cgc = cd_cgc_w))
	and (coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w)
	and	ie_situacao = 'A'
	and	ie_tipo_nota = ie_tipo_nota_p
	and	((cd_operacao_nf = cd_operacao_nf_w) or (coalesce(cd_operacao_nf::text, '') = ''))
	and	((ie_tipo_regra = 'C' AND ie_status_envio_p = '2') or
		(ie_tipo_regra = 'R' AND ie_status_envio_p = '0') or
		(ie_tipo_regra = 'R' AND ie_status_envio_p = '3') or
		(ie_tipo_regra = 'A' AND ie_status_envio_p = '5'));

	if (qt_regra_w = 0) then

		select	count(*)
		into STRICT	qt_regra_w
		from	regra_email_nfe
		where	coalesce(cd_pessoa_fisica::text, '') = ''
		and	coalesce(cd_cgc::text, '') = ''
		and	ie_situacao = 'A'
		and	ie_tipo_nota = ie_tipo_nota_p
		and	((cd_operacao_nf = cd_operacao_nf_w) or (coalesce(cd_operacao_nf::text, '') = ''))
		and (coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w)
		and	((ie_tipo_regra = 'C' AND ie_status_envio_p = '2') or
			(ie_tipo_regra = 'R' AND ie_status_envio_p = '0') or
			(ie_tipo_regra = 'R' AND ie_status_envio_p = '3') or
			(ie_tipo_regra = 'A' AND ie_status_envio_p = '5'));

		if (qt_regra_w > 0) then
			select	max(nr_sequencia)
			into STRICT	nr_seq_regra_nfe_w
			from	regra_email_nfe
			where	coalesce(cd_pessoa_fisica::text, '') = ''
			and	coalesce(cd_cgc::text, '') = ''
			and	ie_situacao = 'A'
			and	ie_tipo_nota = ie_tipo_nota_p
			and	((cd_operacao_nf = cd_operacao_nf_w) or (coalesce(cd_operacao_nf::text, '') = ''))
			and (coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w)
			and	((ie_tipo_regra = 'C' AND ie_status_envio_p = '2') or
				(ie_tipo_regra = 'R' AND ie_status_envio_p = '0') or
				(ie_tipo_regra = 'R' AND ie_status_envio_p = '3') or
				(ie_tipo_regra = 'A' AND ie_status_envio_p = '5'));

		end if;

	else

		select	max(nr_sequencia)
		into STRICT	nr_seq_regra_nfe_w
		from	regra_email_nfe
		where	((cd_pessoa_fisica = cd_pessoa_fisica_w) or (cd_cgc = cd_cgc_w))
		and (coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w)
		and	ie_situacao = 'A'
		and	ie_tipo_nota = ie_tipo_nota_p
		and	((cd_operacao_nf = cd_operacao_nf_w) or (coalesce(cd_operacao_nf::text, '') = ''))
		and	((ie_tipo_regra = 'C' AND ie_status_envio_p = '2') or
			(ie_tipo_regra = 'R' AND ie_status_envio_p = '0') or
			(ie_tipo_regra = 'R' AND ie_status_envio_p = '3') or
			(ie_tipo_regra = 'A' AND ie_status_envio_p = '5'));

	end if;

end if;

return	nr_seq_regra_nfe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
