CREATE OR REPLACE FUNCTION obter_regra_envio_email_compra ( ie_tipo_mensagem_p bigint, cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE



cd_grupo_material_w		smallint;
cd_subgrupo_w			smallint;
cd_classe_material_w		integer;
nr_regras_w			integer;
qt_existe_w			integer;
ie_retorno_w			varchar(1);


BEGIN

ie_retorno_w		:= 'S';

select	count(*)
into STRICT	nr_regras_w
from	regra_envio_email_compra
where	cd_estabelecimento	= cd_estabelecimento_p;

if (nr_regras_w	> 0) then
	begin
	ie_retorno_w		:= 'N';
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_w,
		cd_classe_material_w
	from	estrutura_material_v
	where 	cd_material	= cd_material_p;

	select	count(*)
	into STRICT	qt_existe_w
	from	regra_envio_email_compra
	where	cd_estabelecimento					= cd_estabelecimento_p
	and	coalesce(cd_grupo_material, cd_grupo_material_w)	= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_w)		= cd_subgrupo_w
	and	coalesce(cd_classe_material, cd_classe_material_w)	= cd_classe_material_w
	and	coalesce(cd_material, cd_material_p)			= cd_material_p
	and	ie_tipo_mensagem					= ie_tipo_mensagem_p;

	if (qt_existe_w > 0) then
		ie_retorno_w		:= 'S';
	end if;
	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
