CREATE OR REPLACE FUNCTION obter_regra_etapa_gedipa (nr_seq_regra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


--ie_opcao_p --> N = nome; C = cor
DS_NOME_REGRA_W		varchar(50);
DS_COR_W			varchar(15);

BEGIN

if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	select	DS_NOME_REGRA,
			DS_COR
	into STRICT	DS_NOME_REGRA_W,
			DS_COR_W
	FROM REGRA_ETAPA_GEDIPA
	WHERE NR_SEQUENCIA = nr_seq_regra_p;
end if;

if (ie_opcao_p = 'N') then
	return	coalesce(DS_NOME_REGRA_W,'');
else
	return	coalesce(DS_COR_W,'');
end	if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
