SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_exame_status (ie_status_atend_p text) RETURNS varchar AS $body$
DECLARE


  ds_w varchar(255);
  nr_expressao_w valor_dominio.cd_exp_valor_dominio%type;


BEGIN

select substr(max(w.ds_status),1,255)
into STRICT   ds_w
from   regra_exame_status w
where  coalesce(w.ie_situacao, 'A') = 'A'
and    w.ie_status_regra = ie_status_atend_p;

if (coalesce(ds_w::text, '') = '') then
   begin
      select y.cd_exp_valor_dominio
      into STRICT   nr_expressao_w
      from   valor_dominio_v y
      where  y.cd_dominio = 1030
      and    y.vl_dominio = ie_status_atend_p;
   exception
   when no_data_found then
      return null;
   end;

   select substr(obter_desc_expressao(nr_expressao_w),1,255)
   into STRICT   ds_w
;
end if;

return ds_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
