CREATE OR REPLACE FUNCTION obter_regra_exib_evento ( nr_sequencia_p bigint, cd_funcao_ativa_p bigint ) RETURNS bigint AS $body$
DECLARE


ie_regra_repeticao_w	bigint;


BEGIN

select	coalesce(max(c.ie_regra_repeticao),0)
into STRICT	ie_regra_repeticao_w
from 	ev_evento e,
	ev_evento_regra_dest d,
	ev_regra_dest_tela c
where 	e.nr_sequencia 		= d.nr_seq_evento
and 	d.nr_sequencia 		= c.nr_seq_regra_dest
and	c.ie_funcao		= cd_funcao_ativa_p
and	e.nr_sequencia		= nr_sequencia_p;

return	ie_regra_repeticao_w;

end;
$body$
LANGUAGE PLPGSQL
