CREATE OR REPLACE FUNCTION obter_regra_fatura_parcial (ie_tipo_recebimento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
count_w				bigint;

/* Function criada para verificar a existencia da regra, conforme OS 830247, na especificação anexada. Segundo o analista, a vigencia e nenhum outro campo deve ser verificado, apenas o tipo recebimento*/

BEGIN

if (ie_tipo_recebimento_p IS NOT NULL AND ie_tipo_recebimento_p::text <> '') then

	select	count(*)
	into STRICT	count_w
	from	regra_fatura_parc a
	where	ie_tipo_recebimento =  ie_tipo_recebimento_p;

	if ( count_w > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
