CREATE OR REPLACE FUNCTION obter_regra_fatur_direto_mat (cd_material_p bigint, cd_convenio_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
ie_faturamento_direto_w		varchar(5) := 'S';
ie_faturamento_direto_ww	varchar(5) := 'S';
nr_seq_familia_w		bigint;

C01 CURSOR FOR
	SELECT	ie_faturamento_direto
	from	regra_fatur_direto_mat
	where	coalesce(cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w)		= cd_classe_material_w
	and	coalesce(cd_material,cd_material_p)				= cd_material_p
	and	coalesce(nr_seq_familia,nr_seq_familia_w)			= nr_seq_familia_w
	and	coalesce(cd_convenio,cd_convenio_p)				= cd_convenio_p
	and	coalesce(dt_referencia_p,clock_timestamp()) between coalesce(dt_inicio_vigencia,clock_timestamp() - interval '9999 days') and coalesce(dt_fim_vigencia,clock_timestamp() + interval '999 days')
	order by coalesce(cd_material,0),
		coalesce(nr_seq_familia,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(cd_convenio,0),
		coalesce(dt_inicio_vigencia,clock_timestamp() - interval '9999 days');

BEGIN

select	coalesce(max(cd_grupo_material),0),
	coalesce(max(cd_subgrupo_material),0),
	coalesce(max(cd_classe_material),0),
	coalesce(max(nr_seq_familia),0)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	nr_seq_familia_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_faturamento_direto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_faturamento_direto_ww := ie_faturamento_direto_w;
	end;
end loop;
close C01;

return	ie_faturamento_direto_ww;

end;
$body$
