CREATE OR REPLACE FUNCTION obter_regra_geracao_mapa_setor ( cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_opcao_w	varchar(1);


BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then

	select	coalesce(max(ie_opcao),'I')
	into STRICT	ie_opcao_w
	from	mapa_regra_setor
	where	cd_setor_atendimento = cd_setor_atendimento_p
	and	ie_situacao = 'A';

end if;

return	ie_opcao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
