CREATE OR REPLACE FUNCTION obter_regra_inf_padrao_nf ( cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_aplicacao_p text, ie_campo_retorno_w text) RETURNS varchar AS $body$
DECLARE


/*
ie_aplicacao_p	Passar em qual momento está gerando a nota fiscal
CP	Gerando a nota pela conta paciente
PC	Gerando a nota pelo protocolo convênio
LP	Gerando a nota pelo lote de protocolo

------------------
ie_campo_retorno_w	Dizer qual é o campo que precisa retornar
OBS	DS_OBS_PADRAO
*/
ds_obs_padrao_w		regra_padrao_nf.ds_obs_padrao%type;
ds_retorno_w		varchar(2000);


BEGIN

select	max(ds_obs_padrao)
into STRICT	ds_obs_padrao_w
from	regra_padrao_nf
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_convenio, cd_convenio_p) = cd_convenio_p
and	((ie_aplicacao_p = 'CC' AND ie_nf_conta_paciente = 'S') or
	 (ie_aplicacao_p = 'PC' AND ie_nf_prot_convenio = 'S') or
	 (ie_aplicacao_p = 'LP' AND ie_nf_lote_prot_convenio = 'S'));

if (ie_campo_retorno_w = 'OBS') then
	ds_retorno_w := ds_obs_padrao_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
