CREATE OR REPLACE FUNCTION obter_regra_integra_mat ( cd_estabelecimento_p bigint, cd_classe_material_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
nr_seq_familia_w			integer;
ie_integra_w				varchar(1) := 'N';
ie_padronizado_w			varchar(15);
ie_estoque_w				varchar(15);

c01 CURSOR FOR
SELECT	coalesce(ie_integra,'N')
from	regra_integracao_material
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	((coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = ''))
and	((coalesce(ie_estoque,'T') = 'T') or
	((coalesce(ie_estoque,'T') = 'S') and (ie_estoque_w = 'S')) or
	((coalesce(ie_estoque,'T') = 'N') and (ie_estoque_w = 'N')))
and	((coalesce(ie_padronizado,'T') = 'T') or
	((coalesce(ie_padronizado,'T') = 'S') and (ie_padronizado_w = 'S')) or
	((coalesce(ie_padronizado,'T') = 'N') and (ie_padronizado_w = 'N')))

order by
	coalesce(cd_material, 0),
	coalesce(nr_seq_familia,0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	a.nr_seq_familia,
	b.ie_padronizado,
	b.ie_material_estoque
into STRICT	nr_seq_familia_w,
	ie_padronizado_w,
	ie_estoque_w
from	estrutura_material_v a,
	material_estab b
where	a.cd_material = b.cd_material
and	b.cd_estabelecimento = cd_estabelecimento_p
and	a.cd_material = cd_material_p;

select	a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_classe_material = cd_classe_material_p
and	a.cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_integra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_integra_w := ie_integra_w;
	end;
end loop;
close C01;


return ie_integra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
