CREATE OR REPLACE FUNCTION obter_regra_integra_syspec (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(2) := 'N';
qt_regra_classif_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_classif_w
from	regra_integra_syspec
where (cd_estabelecimento = cd_estabelecimento_p or coalesce(cd_estabelecimento::text, '') = '')
