SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_int_opme_material ( cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_permite_w			varchar(1)	:= 'S';
ie_gera_nota_w			varchar(1)	:= 'S';
ie_exige_motivo_baixa_w		varchar(1)	:= 'N';
qt_regra_w			integer;
ie_possui_regra_w		integer;
cd_material_w			integer;
ie_consignado_w			varchar(1);
ie_consignado_ww		varchar(1);

/*
ie_opcao_p
	01	= Nota
	02	= Baixa
	03	= Verifica se item estÃ¡ na regra
*/
c01 CURSOR FOR
SELECT	ie_gera_nota,
	ie_exige_motivo_baixa,
	ie_consignado
from	regra_int_opme_material
where	coalesce(cd_grupo_material, cd_grupo_material_w)     		= cd_grupo_material_w
and (coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w or cd_subgrupo_material_w = 0)
and (coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w or cd_classe_material_w = 0)
and (coalesce(cd_material, cd_material_p) = cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);

c02 CURSOR FOR
SELECT	count(*)
from	regra_int_opme_material
where	coalesce(cd_grupo_material, cd_grupo_material_w)     	= cd_grupo_material_w
and (coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w or cd_subgrupo_material_w = 0)
and (coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w or cd_classe_material_w = 0)
and (coalesce(cd_material, cd_material_p) = cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

ie_permite_w	:= 'S';

select	count(*)
into STRICT	qt_regra_w
from	regra_int_opme_material;

if (qt_regra_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material	= cd_material_p;

	if (ie_opcao_p = '01') or (ie_opcao_p = '02') or (ie_opcao_p = '03') then
		begin
		open c01;
		loop
		fetch c01 into
			ie_gera_nota_w,
			ie_exige_motivo_baixa_w,
			ie_consignado_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

			if (ie_opcao_p = '01') then
				ie_permite_w	:= ie_gera_nota_w;
			elsif (ie_opcao_p = '02') then
				ie_permite_w	:= ie_exige_motivo_baixa_w;
			elsif (ie_opcao_p = '03') then
				if (ie_consignado_w = 'S') then

					select	ie_consignado
					into STRICT	ie_consignado_ww
					from	material
					where	cd_material = cd_material_p;

					if (ie_consignado_ww = '0') then
						ie_permite_w	:= 'N';
					end if;
				end if;
			end if;

		end loop;
		close c01;
		end;
	else
		begin
		open c02;
		loop
		fetch c02 into
			ie_possui_regra_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */

			if (ie_possui_regra_w = 0) then
				ie_permite_w	:= 'N';
			end if;
		end loop;
		close c02;
		end;
	end if;
	end;
end if;

return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
