CREATE OR REPLACE FUNCTION obter_regra_lanc_conta_adep ( cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_momento_p text) RETURNS varchar AS $body$
DECLARE


ie_regra_conta_adep_w	varchar(15) := 'N';
ie_regra_lancamento_w	varchar(15);
ie_momento_lancamento_w	varchar(15);


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (ie_momento_p IS NOT NULL AND ie_momento_p::text <> '') then

	CALL Wheb_assist_pck.set_informacoes_usuario(cd_estabelecimento_p, cd_perfil_p, nm_usuario_p);
	ie_regra_lancamento_w	:= Wheb_assist_pck.obterParametroFuncao(1113, 49);
	ie_momento_lancamento_w	:= Wheb_assist_pck.obterParametroFuncao(1113, 97);

	if (ie_momento_p			= 'PP') and (ie_momento_lancamento_w	= 'PN') then
		ie_regra_conta_adep_w	:= 'R';
	elsif (ie_momento_lancamento_w = 'CR') or (ie_momento_p = ie_momento_lancamento_w) then
		ie_regra_conta_adep_w	:= ie_regra_lancamento_w;
	else
		ie_regra_conta_adep_w	:= 'N';
	end if;

end if;

return ie_regra_conta_adep_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
