CREATE OR REPLACE FUNCTION obter_regra_legenda_medic (cd_material_p bigint, cd_funcao_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_retorno_p:
	-- CF - Cor Fundo
	-- CL - Cor Legenda
*/
cd_classe_material_w		integer;
cd_subgrupo_material_w		smallint;
cd_grupo_material_w			smallint;
ds_cor_fundo_w				varchar(15);
ds_cor_fonte_w				varchar(15);
ds_retorno_w				varchar(18);

C01 CURSOR FOR
	SELECT	max(ds_cor), max(ds_cor_fonte)
	from	regra_material_legenda
	where	coalesce(cd_grupo_material,cd_grupo_material_w)	= cd_grupo_material_w
	and		coalesce(cd_subgrupo_material,cd_subgrupo_material_w)= cd_subgrupo_material_w
	and		coalesce(cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
	and		coalesce(cd_material,cd_material_p)	= cd_material_p
	and		coalesce(cd_funcao,cd_funcao_p)	= cd_funcao_p;


BEGIN
select 	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
from 	estrutura_material_v
where	cd_material = cd_material_p;

open C01;
fetch C01 into
	ds_cor_fundo_w,
	ds_cor_fonte_w;
close C01;

if (ie_retorno_p = 'CF') then
	ds_retorno_w := ds_cor_fundo_w;
elsif (ie_retorno_p = 'CL')	then
	ds_retorno_w := ds_cor_fonte_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
