CREATE OR REPLACE FUNCTION obter_regra_lib_tipo_pessoa ( cd_tipo_pessoa_p bigint, cd_perfil_p bigint, nm_usuario_lib_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_alterar_w		varchar(1) := 'S';
ie_resultado_w		varchar(1) := 'S';

c01 CURSOR FOR
SELECT	ie_alterar
from	tipo_pessoa_jur_lib
where	cd_tipo_pessoa			= cd_tipo_pessoa_p
and	coalesce(cd_perfil, cd_perfil_p)		= cd_perfil_p
and	coalesce(nm_usuario_lib, nm_usuario_lib_p)	= nm_usuario_lib_p
order by
	coalesce(cd_perfil,99999) desc,
	coalesce(nm_usuario_lib, 'XXXX') desc;



BEGIN

open C01;
loop
fetch C01 into
	ie_alterar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 0) then
		ie_resultado_w := ie_alterar_w;
	end if;

	end;
end loop;
close C01;


return	ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
