CREATE OR REPLACE FUNCTION obter_regra_local_devolucao ( cd_material_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
qt_regra_w			integer;
cd_local_estoque_w		smallint;
nr_seq_familia_w		bigint;

c01 CURSOR FOR
SELECT	cd_local_estoque
from	regra_local_devolucao
where	cd_estabelecimento					= cd_estabelecimento_p
and	((coalesce(cd_setor_atendimento, cd_setor_atendimento_p)	= cd_setor_atendimento_p) or (coalesce(cd_setor_atendimento_p::text, '') = ''))
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) = cd_material_p or cd_material_p = 0)
and	((coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = ''))
and	((coalesce(ie_considerar_padrao_est,'N') = 'N' or obter_se_mat_existe_padrao_loc(cd_material_p,cd_local_estoque) = 'S') or (coalesce(ie_considerar_controlador,'N') = 'S' and obter_se_mat_existe_padrao_loc((	select	x.cd_material_estoque
											from	material x
											where	x.cd_material = cd_material_p),cd_local_estoque) = 'S'))
order by coalesce(nr_prioridade,999) desc,
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_local_devolucao;

if (qt_regra_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		nr_seq_familia
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w,
		nr_seq_familia_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	open c01;
	loop
	fetch c01 into
		cd_local_estoque_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;
       	end;
end if;

return	cd_local_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
