CREATE OR REPLACE FUNCTION obter_regra_local_solic ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1) := 'S';
cd_perfil_ativo_w			integer;

c01 CURSOR FOR
SELECT	ie_permite
from	sup_regra_local_solic
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_local_estoque,cd_local_estoque_p) = cd_local_estoque_p
and (cd_perfil = cd_perfil_ativo_w or coalesce(cd_perfil::text, '') = '')
order by	coalesce(cd_perfil,99999) desc,
		coalesce(cd_local_estoque,99999) desc;



BEGIN

cd_perfil_ativo_w		:= obter_perfil_ativo;

open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_permite_w := ie_permite_w;
	end;
end loop;
close C01;


return	ie_permite_w;

end	;
$body$
