SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regra_local_usuario ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, ie_evento_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE



ie_tipo_w			varchar(1);
ie_permissao_w		varchar(1);
ie_regra_local_usuario_w	varchar(1);
cd_cargo_w		integer;
qt_existe_w		integer;
cd_perfil_w		integer;

c01 CURSOR FOR
SELECT	coalesce(ie_permissao,'S')
from	permissao_local_usuario c,
	evento_local_usuario b,
	regra_local_usuario a
where	a.nr_sequencia		= b.nr_seq_regra_local
and	b.nr_sequencia		= c.nr_seq_regra_evento
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.cd_local_estoque		= cd_local_estoque_p
and	b.ie_evento		= ie_evento_p
and	coalesce(c.nm_usuario_lib, nm_usuario_p) 	= nm_usuario_p
and	coalesce(c.cd_cargo_lib, cd_cargo_w) 	= cd_cargo_w
and	coalesce(c.cd_perfil, cd_perfil_w) 		= cd_perfil_w
order by c.nm_usuario_lib desc,
	  c.cd_cargo_lib desc,
	  c.cd_perfil desc;


BEGIN
/*padrão eh liberado*/

ie_permissao_w		:= 'S';

select	coalesce(max(ie_regra_local_usuario),'N')
into STRICT	ie_regra_local_usuario_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;
if (ie_regra_local_usuario_w = 'S') then
	begin
	/*se estiver identificado nos parametros estoque, não eh liberado*/

	ie_permissao_w	:= 'N';

	/*se não existir regra para o local especifico, eh liberado*/

	select	count(*)
	into STRICT	qt_existe_w
	from	regra_local_usuario a,
		evento_local_usuario b
	where	a.nr_sequencia 		= b.nr_seq_regra_local
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.cd_local_estoque		= cd_local_estoque_p
	and	b.ie_evento		= ie_evento_p;

	if (qt_existe_w = 0) then
		ie_permissao_w		:= 'S';
	else
		begin

		cd_perfil_w := obter_perfil_ativo;

		select	coalesce(max(cd_cargo),0)
		into STRICT	cd_cargo_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = (
			SELECT	max(cd_pessoa_fisica)
			from	usuario
			where	nm_usuario = nm_usuario_p);

		open	c01;
		loop
		fetch	c01 into
			ie_permissao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		end loop;
		close 	c01;
		end;
	end if;
	end;
end if;

return ie_permissao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
