CREATE OR REPLACE FUNCTION obter_regra_manguito ( cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

qt_anos_w		bigint;
qt_dias_w		bigint;
ie_manguito_w		varchar(15);


BEGIN

select 	Obter_Idade_PF(cd_pessoa_fisica_p,clock_timestamp(),'A'),
	Obter_Idade_PF(cd_pessoa_fisica_p,clock_timestamp(),'DIA')
into STRICT	qt_anos_w,
	qt_dias_w
;


select	max(ie_manguito)
into STRICT	ie_manguito_w
from	regra_manguito
where	qt_anos_w between qt_idade_min and qt_idade_max;


if (coalesce(ie_manguito_w::text, '') = '')	then
	select	max(ie_manguito)
	into STRICT	ie_manguito_w
	from	regra_manguito
	where	qt_dias_w between qt_idade_min_dias and qt_idade_max_dias;
end if;

if (coalesce(ie_manguito_w::text, '') = '') then
	ie_manguito_w := 'C';
end if;


return	ie_manguito_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
