CREATE OR REPLACE FUNCTION obter_regra_material_perfil ( cd_material_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w			regra_material_perfil.nr_sequencia%type;
cd_grupo_material_w		grupo_material.cd_grupo_material%type;
cd_subgrupo_material_w		subgrupo_material.cd_subgrupo_material%type;
cd_classe_material_w		classe_material.cd_classe_material%type;
nr_seq_familia_w		material_familia.nr_sequencia%type;
cd_perfil_w			perfil.cd_perfil%type;
ie_tipo_material_w	regra_mat_perfil_estrut.ie_tipo_material%type;
ie_visualizar_w			varchar(1)	:= 'S';
ie_regra_w			varchar(1)	:= 'N';

c00 CURSOR FOR
SELECT	nr_sequencia
from	regra_material_perfil
where	coalesce(cd_perfil,cd_perfil_w)			= cd_perfil_w
and	coalesce(nm_usuario_regra,nm_usuario_p)		= nm_usuario_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p
order by
	nm_usuario_regra DESC,
	cd_perfil DESC,
	cd_estabelecimento DESC,
	cd_setor_atendimento DESC;

c01 CURSOR FOR
SELECT	ie_visualizar
from	regra_mat_perfil_estrut
where	nr_seq_regra						= nr_seq_regra_w
and	coalesce(cd_grupo_material, cd_grupo_material_w)     	= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w
and coalesce(ie_tipo_material, ie_tipo_material_w)			= ie_tipo_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(ie_tipo_material, 0),
	coalesce(nr_seq_familia,0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

ie_visualizar_w		:= 'S';
cd_perfil_w		:= obter_perfil_ativo;

begin
select	'S'
into STRICT	ie_regra_w
from	regra_material_perfil LIMIT 1;
exception
when OTHERS then
	ie_regra_w := 'N';
end;

nr_seq_regra_w	:= 0;

if (ie_regra_w = 'S') then
	begin

	open c00;
	loop
	fetch c00 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on c00 */
	end loop;

	exception
	when OTHERS then
		nr_seq_regra_w := 0;
	end;

end if;


if (nr_seq_regra_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		coalesce(nr_seq_familia,0),
		ie_tipo_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w,
		nr_seq_familia_w,
		ie_tipo_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	open c01;
	loop
	fetch c01 into
                ie_visualizar_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;
	end;
end if;

return ie_visualizar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
